import "./RadarStyle.css";
import * as echarts from 'echarts';
import { useRef ,useEffect, useState} from "react";
export default function Radar(){
    const chartRef = useRef(null);
    var mychart = [];
    const option = {
        title: {
          // text: 'Basic Radar Chart'
        },
        legend: {
          // data: ['Allocated Budget', 'Actual Spending']
        },
        radar: {
          // shape: 'circle',
          indicator: [
            // { name: 'HSJA_L2', max: 35000 },
            // { name: 'Bounce_L2', max: 35000 },
            // { name: 'Sign-OPT_L2', max: 35000 },
            // { name: 'RamBo_L2', max: 35000 },
            // { name: 'Rays_L2', max: 35000 },
            // { name: 'SurFree_L2', max: 35000 },
            // { name: 'AHA_L2', max: 35000 },
            
            { name: 'HSJA_L∞', max: 35000 },
            { name: 'Bounce_L∞', max: 35000 },
            { name: 'Sign-OPT_L∞', max: 35000 },
            { name: 'RamBo_L∞', max: 35000 },
            { name: 'Rays_L∞', max: 35000 },
            { name: 'SurFree_L∞', max: 35000 },
            { name: 'AHA_L∞', max: 35000 },
          ]
        },
        series: [
          {
            name: 'Budget vs spending',
            type: 'radar',
            data: [
              // {
                // value: [15000, 35000, 20000, 30000, 30000, 18000, 12000],
                // name: 'Allocated Budget'
              // },
              {
                value: [33000, 14000, 28000, 26000, 18000, 20000, 16000],
                // name: 'Actual Spending'
              }
            ]
          }
        ]
      };

      useEffect(()=>{
        mychart = echarts.init(chartRef.current);
        mychart.setOption(option);
      },[]);
    return(
        <div id="radar">
            <div ref={chartRef} style={{ width: '70%', height: "100%" }}></div>
        </div>
    );
}
